mapper.select:
SELECT

FROM bmf_domain_relation
where business_code = #{businessCode,jdbcType=INTEGER}
and (
    domain_a_code = #{domainACode,jdbcType=INTEGER}
    or
    domain_b_code = #{domainBCode,jdbcType=INTEGER}
)
and is_deleted = 0

mapper.insert:
insert into bmf_domain_relation (business_code, domain_relation, domain_a_code, domain_b_code, domain_a_role, domain_b_role) values
#{item.businessCode,jdbcType=INTEGER}, #{item.domainRelation,jdbcType=VARCHAR}, #{item.domainACode,jdbcType=INTEGER}, #{item.domainBCode,jdbcType=INTEGER}, #{item.domainARole,jdbcType=VARCHAR}, #{item.domainBRole,jdbcType=VARCHAR}
on duplicate key update domain_relation = values(domain_relation), domain_a_role = values(domain_a_role), domain_b_role = values(domain_b_role), gmt_modify = now()

mapper.delete:
delete from bmf_domain_relation


        and business_code = #{businessCode}


        and domain_relation = #{domainRelation}


        and domain_a_code = #{domainACode}


        and domain_b_code = #{domainBCode}


        and domain_a_role = #{domainARole}


        and domain_b_role = #{domainBRole}
